import React, { useState } from 'react';

const App = () => {
  const [sandwich, setSandwich] = useState({
    topping: '',
    bread: '',
    condiment: '',
  });

  const toppings = ['Roast beef', 'Egg og avokado', 'Skinka og tomat'];
  const breads = ['Chiabreyð', 'Fullkornsbreyð'];
  const condiments = ['Dijon sinnoppur', 'Majones', 'Grønt pesto', 'Reytt pesto'];

  const generateSandwich = () => {
    const newTopping = toppings[Math.floor(Math.random() * toppings.length)];
    const newBread = breads[Math.floor(Math.random() * breads.length)];
    const newCondiment = condiments[Math.floor(Math.random() * condiments.length)];
    setSandwich({
      topping: newTopping,
      bread: newBread,
      condiment: newCondiment,
    });
  };

  return (
    <div>
      <button onClick={generateSandwich}>Generate Sandwich</button>
      <p>
        Pálegg: {sandwich.topping}
      </p>
      <p>
        Bread: {sandwich.bread}
      </p>
      <p>
        Condiment: {sandwich.condiment}
      </p>
    </div>
  );
};

export default App;
