import React, { useState } from 'react';

const App = () => {
  const [sandwiches, setSandwiches] = useState([]);
  const [numSandwiches, setNumSandwiches] = useState(1);

  const toppings = ['Smoked salmon', 'Egg and avocado', 'Ham and cheese'];
  const breads = ['Rye bread', 'Whole grain bread', 'White bread'];
  const condiments = ['Dijon mustard', 'Mayonnaise', 'Creme fraiche'];

  const generateSandwiches = () => {
    const newSandwiches: { topping: string, bread: string, condiment: string }[] = []; // fix: specify element type
    for (let i = 0; i < numSandwiches; i++) {
      const topping = toppings[Math.floor(Math.random() * toppings.length)];
      const bread = breads[Math.floor(Math.random() * breads.length)];
      const condiment = condiments[Math.floor(Math.random() * condiments.length)];
      newSandwiches.push({ topping, bread, condiment });
    }
    setSandwiches(newSandwiches);
  };

  return (
    <div>
      <label htmlFor="num-sandwiches">Number of sandwiches:</label>
      <input
        id="num-sandwiches"
        type="number"
        value={numSandwiches}
        onChange={(e) => setNumSandwiches(Number(e.target.value))}
      />
      <button onClick={generateSandwiches}>Generate Sandwiches</button>
      {sandwiches.map((sandwich, index) => (
        <div key={index}>
          <p>
            Topping: {sandwich.topping}
          </p>
          <p>
            Bread: {sandwich.bread}
          </p>
          <p>
            Condiment: {sandwich.condiment}
          </p>
        </div>
      ))}
    </div>
  );
};

export default App;
